document.getElementById("contactForm").addEventListener("submit",(function(event){const email=document.getElementById("email").value;const name=document.getElementById("name").value;const emailRegex=/^[^\s@]+@[^\s@]+\.[^\s@]+$/;const nameRegex=/^[a-zA-Z\s]{1,50}$/;if(!emailRegex.test(email)){alert("Please enter a valid email address.");event.preventDefault()}if(!nameRegex.test(name)){alert("Name should only contain letters and spaces, up to 50 characters.");event.preventDefault()}}));